/* Team Model
*/
var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var inviteSchema = mongoose.Schema({ 
	invite: {type:String, unique: true},
	email: String, 
	persons : [{
		id : Number,
		name : String,
	    registered : {type: Boolean }
	}],

});

module.exports = mongoose.model('invites', inviteSchema);